import React, { useState, useEffect } from 'react';

const Statistics = () => {
  const [seasonStats, setSeasonStats] = useState([]);

  useEffect(() => {
    // Effectuer une requête vers l'URL du service web pour récupérer les statistiques de la saison
    fetch('URL_DU_WS')
      .then(response => response.json())
      .then(data => setSeasonStats(data))
      .catch(error => console.error('Erreur lors de la récupération des données:', error));
  }, []); // Le tableau vide en tant que dépendance signifie que cet effet ne s'exécute qu'une fois après le montage du composant

  return (
    <div>
      <h1>2023-2024 SAISON STATS</h1>
      <table>
        <thead>
          {/* ... En-têtes de colonnes ... */}
        </thead>
        <tbody>
          {seasonStats.map((playerStat, index) => (
            <PlayerStatsRow key={index} {...playerStat} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

const PlayerStatsRow = ({
  ranking,
  playerName,
  team,
  matches,
  matchesPlayed,
  ppm,
  rpm,
  pdpm,
  mpm,
  eff,
  fgPercentage,
  threePPercentage,
  freeThrowPercentage,
  isDisabled,
  defense,
  ipm,
  cpm,
  bp,
  fp,
}) => {
  return (
    <tr>
      <td>{ranking}</td>
      <td>{playerName}</td>
      <td>{team}</td>
      <td>{matches}</td>
      <td>{matchesPlayed}</td>
      <td>{ppm}</td>
      {/* ... autres propriétés ... */}
    </tr>
  );
};

export default Statistics;
